mapper.select:
/* kr.happyjob.study.repository.menu.MenuMapper.mainmenusearch */
select main.mnu_id ,main.hir_mnu_id ,main.mnu_nm ,main.mnu_url ,main.mnu_dvs_cod ,main.grp_num ,main.odr ,main.lvl ,main.mnu_ico_cod ,main.use_poa ,main.dlt_poa ,( select count(*) as cnt from tm_mnu_mst sub where sub.hir_mnu_id = main.mnu_id and sub.lvl = 1 ) as submenucnt from tm_mnu_mst main
and main.mnu_id Like CONCAT('%', #{smenuid}, '%')
and main.mnu_nm Like CONCAT('%', #{smenuname}, '%')
and main.use_poa = #{suseyn}
and main.lvl = #{level}
order by main.hir_mnu_id, main.mnu_id limit #{pageIndex}, #{pageSize}

			/* kr.happyjob.study.repository.menu.MenuMapper.mainmenusearchtotalcnt */

        select count(*) as cnt
         from tm_mnu_mst


			    and mnu_id  Like CONCAT('%', #{smenuid}, '%')


			    and mnu_nm  Like CONCAT('%', #{smenuname}, '%')


			    and use_poa = #{suseyn}

			and lvl = #{level}

			/* kr.happyjob.study.repository.menu.MenuMapper.submenusearchtotalcnt */

        select count(*) as cnt
          from tm_mnu_mst
         where hir_mnu_id = #{smenuid}
		   and lvl = #{level}

			/* kr.happyjob.study.repository.menu.MenuMapper.mainmenudupcheck */

        select count(*) as cnt
          from tm_mnu_mst
         where mnu_id = #{mnu_id}

mapper.update:
update tm_mnu_mst
   set mnu_nm = #{mnu_nm}
      ,use_poa = #{use_poa}
 where mnu_id = #{menucd}

	   update tm_mnu_mst
	      set mnu_nm = #{sub_mnu_nm}
             ,mnu_url = #{url} 
 	         ,use_poa = #{use_poa}
	    where mnu_id = #{sub_mnu_id}

mapper.delete:
delete from tn_usr_mnu_atrt
where  MNU_ID = #{submenuid}
   and  user_type in ('A','B')

mapper.select:
select count(*) as cnt
 from tn_usr_mnu_atrt
where mnu_id in (
                           select mnu_id
                            from tm_mnu_mst
                           where HIR_MNU_ID = #{mainmenuid}
                              and lvl = 1
                       )    
   and user_type = #{Usertype}

mapper.delete:
delete from tn_usr_mnu_atrt
where mnu_id = #{mainmenuid}
   and user_type = #{Usertype}

mapper.select:
select count(*) as cnt
   from tn_usr_mnu_atrt
  where mnu_id  = #{mainmenuid}
     and user_type = #{Usertype}
