mapper.select:
/* ApplyMapper.applyList */ SELECT DISTINCT pd.product_detail_code, pi.category_code, pi.product_name, pd.product_state, COALESCE(cc.category_name, pi.category_code) as category_name, pd.order_reason, pd.loginID, ui.name as applicant_name FROM tb_product_detail pd JOIN tb_product_info pi ON pi.product_no = pd.product_no LEFT JOIN tb_category_code cc ON cc.category_code = pi.category_code LEFT JOIN tb_userinfo ui ON ui.loginID = pd.loginID
AND pd.product_state = #{filterState}
AND ( pd.product_state = 'N' OR (pd.product_state IN ('Y', 'O', 'R') AND pd.loginID = #{loginIdFilter}) )
AND pd.product_state = #{filterState}
AND pi.product_name LIKE CONCAT('%', #{searchword}, '%')
AND (cc.category_name LIKE CONCAT('%', #{searchword}, '%') OR pi.category_code = #{searchword})
AND pi.category_code = #{searchword}
AND pd.product_state = #{searchword}
ORDER BY pd.product_detail_code DESC LIMIT #{pageIndex}, #{pageSize}

SELECT COUNT(pd.product_detail_code)
FROM tb_product_detail pd
JOIN tb_product_info pi ON pi.product_no = pd.product_no
LEFT JOIN tb_category_code cc ON cc.category_code = pi.category_code







                AND pd.product_state = #{filterState}  



                AND (
                    pd.product_state = 'N'  
                    OR (pd.product_state IN ('Y', 'O', 'R') AND pd.loginID = #{loginIdFilter}) 
                )






                AND pd.product_state = #{filterState}







                AND pi.product_name LIKE CONCAT('%', #{searchword}, '%')


                AND (cc.category_name LIKE CONCAT('%', #{searchword}, '%') OR pi.category_code = #{searchword})


                AND pi.category_code = #{searchword}


                AND pd.product_state = #{searchword}

mapper.update:

UPDATE tb_product_detail
SET product_state = 'O',
    order_date = NOW(),
    order_reason = #{order_reason},
    loginID = #{loginID}
WHERE product_detail_code = #{product_detail_code}

UPDATE tb_product_detail
SET product_state = 'R',
    return_date = NOW(),
    order_reason = #{order_reason}
WHERE product_detail_code = #{product_detail_code}
