mapper.select:
/* ApplyMapper.applyList */ SELECT pd.product_detail_code, pi.category_code, pi.product_name, pd.product_state, COALESCE(cc.category_name, pi.category_code) as category_name, pd.order_reason FROM tb_product_detail pd JOIN tb_product_info pi ON pi.product_no = pd.product_no LEFT JOIN tb_category_code cc ON cc.category_code = pi.category_code
AND pi.product_name LIKE CONCAT('%', #{searchword}, '%')
AND (cc.category_name LIKE CONCAT('%', #{searchword}, '%') OR pi.category_code = #{searchword})
AND pd.product_state = #{searchword}
ORDER BY pd.product_detail_code DESC LIMIT #{pageIndex}, #{pageSize}

mapper.insert:
INSERT INTO tb_use_history (
    category_code, product_detail_code, rental_date, return_date, name, loginID
) VALUES (
             #{category_code}, #{product_detail_code}, NOW(), NULL, #{name}, #{loginID}
         )

mapper.update:
UPDATE tb_product_detail
SET product_state = 'R', return_date = NOW()
WHERE product_detail_code = #{product_detail_code}
